mapper.update:
UPDATE users
nickName = #{nickName}, email = #{email}, phoneNumber = #{phoneNumber}, password = SHA2(CONCAT('', #{password}), 256)
WHERE userId = #{userId}

mapper.select:
SELECT places.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d %H:%i') as createdAt FROM places JOIN placesFavorite ON places.placeName = placesFavorite.placeName JOIN placeImages ON places.placeName = placeImages.placeName WHERE placesFavorite.userId = #{userId}
AND upper(TRIM(REPLACE(places.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%')
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d') = #{calendarMin}
ORDER BY placesFavorite.createdTime DESC
LIMIT #{startRowValue}, #{rowCnt}

SELECT places.placeName, reviews.review, DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d %H:%i') AS createdAt, placeImages.firstUrl,
users.nickName AS reviewAuthor FROM places JOIN reviews ON places.placeName = reviews.placeName JOIN reviewLikes ON reviews.placeName = reviewLikes.placeName AND reviews.userId = reviewLikes.userId JOIN placeImages ON places.placeName = placeImages.placeName JOIN users ON reviewLikes.userId = users.userId WHERE reviewLikes.likeUserId = #{userId}
AND ( upper(TRIM(REPLACE(reviews.review, ' ', ''))) LIKE upper('%${searchKeyword}%') OR upper(TRIM(REPLACE(places.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%') )
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d') = #{calendarMin}
ORDER BY reviews.createdAt DESC
LIMIT #{startRowValue}, #{rowCnt}

SELECT placeImages.firstUrl, places.placeName, CONCAT(places.largeAddress, ' ',places.midiumAddress) AS address, reviews.review, DATE_FORMAT(reviews.modifiedAt, '%Y.%m.%d %H:%i') AS createdAt FROM placeImages JOIN places ON placeImages.placeName = places.placeName JOIN reviews ON places.placeName = reviews.placeName WHERE reviews.userId = '${ userId }'
AND ( upper(TRIM(REPLACE(reviews.review, ' ', ''))) LIKE upper('%${searchKeyword}%') OR upper(TRIM(REPLACE(places.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%') )
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(reviews.createdAt, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(reviews.createdAt, '%Y.%m.%d') = #{calendarMin}
ORDER BY reviews.createdAt DESC
LIMIT #{startRowValue}, #{rowCnt}

SELECT placeImages.firstUrl, placeVerified.placeName, DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d %H:%i') AS createdAt, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address FROM placeVerified JOIN places ON placeVerified.placeName = places.placeName JOIN placeImages ON placeVerified.placeName = placeImages.placeName WHERE placeVerified.userId = #{userId}
AND upper(TRIM(REPLACE(placeVerified.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%')
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d') = #{calendarMin}
ORDER BY placeVerified.verifiedTime DESC
LIMIT #{startRowValue}, #{rowCnt}

/* 좋아요한 명소들 */ SELECT 'favorite_places' AS type, places.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d %H:%i') AS createdAt, NULL AS review, NULL AS reviewAuthor FROM places JOIN placesFavorite ON places.placeName = placesFavorite.placeName JOIN placeImages ON places.placeName = placeImages.placeName WHERE placesFavorite.userId = '${ userId }'
AND upper(TRIM(REPLACE(places.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%')
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d') = #{calendarMin}
UNION ALL
/* 좋아요한 리뷰들 */ SELECT 'liked_reviews' AS type, places.placeName, NULL AS address, placeImages.firstUrl, DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d %H:%i') AS createdAt, reviews.review,
reviewAuthor.nickName AS reviewAuthor FROM places JOIN reviews ON places.placeName = reviews.placeName JOIN reviewLikes ON reviews.placeName = reviewLikes.placeName AND reviews.userId = reviewLikes.userId JOIN users AS likeUser ON reviewLikes.likeUserId = likeUser.userId JOIN users AS reviewAuthor ON reviews.userId = reviewAuthor.userId JOIN placeImages ON places.placeName = placeImages.placeName WHERE reviewLikes.likeUserId = '${ userId }'
AND ( upper(TRIM(REPLACE(reviews.review, ' ', ''))) LIKE upper('%${searchKeyword}%') OR upper(TRIM(REPLACE(places.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%') )
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d') = #{calendarMin}
UNION ALL
/* 작성한 리뷰들 */ SELECT 'written_reviews' AS type, places.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(reviews.createdAt, '%Y.%m.%d %H:%i') AS createdAt, reviews.review, NULL AS reviewAuthor FROM placeImages JOIN places ON placeImages.placeName = places.placeName JOIN reviews ON places.placeName = reviews.placeName WHERE reviews.userId = '${ userId }'
AND ( upper(TRIM(REPLACE(reviews.review, ' ', ''))) LIKE upper('%${searchKeyword}%') OR upper(TRIM(REPLACE(places.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%') )
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(reviews.createdAt, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(reviews.createdAt, '%Y.%m.%d') = #{calendarMin}
UNION ALL
/* 위치 인증한 장소 */ SELECT 'verified_places' AS type, placeVerified.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d %H:%i') AS createdAt, NULL AS review, NULL AS reviewAuthor FROM placeVerified JOIN places ON placeVerified.placeName = places.placeName JOIN placeImages ON placeVerified.placeName = placeImages.placeName WHERE placeVerified.userId = '${ userId }'
AND upper(TRIM(REPLACE(placeVerified.placeName, ' ', ''))) LIKE upper('%${searchKeyword}%')
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d') BETWEEN #{calendarMin} AND #{calendarMax}
AND DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d') = #{calendarMin}
ORDER BY createdAt DESC, placeName DESC, review DESC
LIMIT #{startRowValue}, #{rowCnt}
