mapper.update:
UPDATE users
nickName = #{nickName}, email = #{email}, phoneNumber = #{phoneNumber}, password = SHA2(CONCAT('', #{password}), 256)
WHERE userId = #{userId}

mapper.select:
SELECT places.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d %H:%i') as createdAt FROM places JOIN placesFavorite ON places.placeName = placesFavorite.placeName JOIN placeImages ON places.placeName = placeImages.placeName WHERE placesFavorite.userId = #{userId}
AND upper(places.placeName) LIKE upper(#{searchKeyword})
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d') = #{calendar}
ORDER BY placesFavorite.createdTime DESC

SELECT places.placeName, reviews.review, DATE_FORMAT(reviews.createdAt, '%Y.%m.%d %H:%i') AS createdAt, placeImages.firstUrl, reviewLikes.userId AS reviewAuthor FROM places JOIN reviews ON places.placeName = reviews.placeName JOIN reviewLikes ON reviews.placeName = reviewLikes.placeName AND reviews.userId = reviewLikes.userId JOIN placeImages ON places.placeName = placeImages.placeName WHERE reviewLikes.likeUserId = #{userId}
AND upper(places.placeName) LIKE upper('%${searchKeyword}%')
AND places.largeAddress = #{largeAddress}
AND DATE_FORMAT(reviews.createdAt, '%Y.%m.%d') = #{calendar}
ORDER BY reviews.createdAt DESC

/* 좋아요한 명소들 */ SELECT 'favorite_places' AS type, places.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(placesFavorite.createdTime, '%Y.%m.%d %H:%i') AS createdAt, NULL AS review, NULL AS likeUserId, NULL AS nickName FROM places JOIN placesFavorite ON places.placeName = placesFavorite.placeName JOIN placeImages ON places.placeName = placeImages.placeName WHERE placesFavorite.userId = '${ userId }'
UNION ALL
/* 좋아요한 리뷰들 */ SELECT 'liked_reviews' AS type, places.placeName, NULL AS address, placeImages.firstUrl, DATE_FORMAT(reviewLikes.createdTime, '%Y.%m.%d %H:%i') AS createdAt, reviews.review, reviewLikes.likeUserId, users.nickName FROM places JOIN reviews ON places.placeName = reviews.placeName JOIN reviewLikes ON reviews.placeName = reviewLikes.placeName AND reviews.userId = reviewLikes.userId JOIN users ON reviewLikes.likeUserId = users.userId JOIN placeImages ON places.placeName = placeImages.placeName WHERE reviewLikes.likeUserId = '${ userId }'
UNION ALL
/* 작성한 리뷰들 */ SELECT 'written_reviews' AS type, places.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(reviews.createdAt, '%Y.%m.%d %H:%i') AS createdAt, reviews.review, NULL AS likeUserId, NULL AS nickName FROM placeImages JOIN places ON placeImages.placeName = places.placeName JOIN reviews ON places.placeName = reviews.placeName WHERE reviews.userId = '${ userId }'
UNION ALL
/* 위치 인증한 장소 */ SELECT 'verified_places' AS type, placeVerified.placeName, CONCAT(places.largeAddress, ' ', places.midiumAddress) AS address, placeImages.firstUrl, DATE_FORMAT(placeVerified.verifiedTime, '%Y.%m.%d %H:%i') AS createdAt, NULL AS review, NULL AS likeUserId, NULL AS nickName FROM placeVerified JOIN places ON placeVerified.placeName = places.placeName JOIN placeImages ON placeVerified.placeName = placeImages.placeName WHERE placeVerified.userId = '${ userId }'
ORDER BY createdAt
LIMIT #{startRowValue}, #{rowCnt}
